          // try to show the last visible main tab...
          $('#settingsMainTab a[href="' + currentMainTab + '"]').tab('show');

          // try to show the last visible sub behaviour tab...
          $('#settingsBehaviourTab a[href="' + currentSubBehaviourTab + '"]').tab('show');

